content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:959:"<p><strong>Product</strong>:</p>\n<p><a href="https://www.wonderplugin.com/wordpress-gallery/" target="_blank">WonderPlugin Gallery</a>, <a href="https://www.wonderplugin.com/wordpress-slider/" target="_blank">WonderPlugin Slider</a>, <a href="https://www.wonderplugin.com/wordpress-gridgallery/" target="_blank">WonderPlugin Portfolio Grid Gallery</a></p>\n<p><strong>Question</strong>:</p>\n<p>Is 8MB the largest upload size you offer with the WordPress portfolio grid gallery? I tried to add a video file but the video was way too large. </p>\n<p><strong>Answer</strong>:</p>\n<p>8MB is a limit from your web server. </p>\n<p>You need to change the web server PHP configuration file to change the limit. If you are using a shared web hosting, you need to contact your web hosting provider and ask them to make the change.</p>\n<p>Or you can upload the video file to your web server via FTP client software, then add the video URL link directly to the plugin.</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}s:27:"http://www.w3.org/2005/Atom";a:1:{s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:3:{s:4:"href";s:34:"https://www.wonderplugin.com/feed/";s:3:"rel";s:4:"self";s:4:"type";s:19:"application/rss+xml";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:44:"http://purl.org/rss/1.0/modules/syndication/";a:2:{s:12:"updatePeriod";a:1:{i:0;a:5:{s:4:"data";s:6:"hourly";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:15:"updateFrequency";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}}}}}}s:4:"type";i:128;s:7:"headers";a:10:{s:4:"date";s:29:"Fri, 12 Feb 2016 14:54:37 GMT";s:6:"server";s:22:"Apache/2.2.15 (CentOS)";s:12:"x-powered-by";s:9:"PHP/5.3.3";s:13:"last-modified";s:29:"Fri,